SELECT pays.Zone, annee.Annee, sum(indicateur_co2_ch4.Emission_de_CO2) as emission_totale_de_CO2 FROM faopollution.indicateur_co2_ch4,faopollution.pays, faopollution.produit, faopollution.annee where pays.CodeZone=indicateur_co2_ch4.CodeZone and produit.CodeProduit=indicateur_co2_ch4.CodeProduit and annee.CodeAnnee=indicateur_co2_ch4.CodeAnnee group by Zone, Annee having Zone in ('Afrique', 'Europe','Asie','Am√©rique');